mapper.select:
select count(*)
from cart
where user_id = #{userId} 
and product_id = #{productId}

select count(*)
from cart
where user_id = #{userId}

mapper.delete:
delete from orderlist
	where user_id = #{userId}
	and product_id = #{productId};

mapper.select:
select * from orderlist 
	where user_id = #{userId}
	and product_id = #{productId};
